package org.xfl.annotation;import java.lang.annotation.Annotation;import java.lang.reflect.Field;import java.lang.reflect.Method;/** * @program: learn * @description: 注解测试类 * @author: xfl * @create: 2018-07-16 14:32 **/public class MyAnnotationMain {    public static void main(String[] args){        Class clazz = MyAnnotationTest.class;        Method[] method = clazz.getDeclaredMethods();        for(Method meth: method){            Annotation[] annotation = meth.getAnnotations();            System.out.println("获取到方法上的标签：" + annotation[0].toString());            Class classes = annotation[0].getClass();            Method[] annoMethod = classes.getDeclaredMethods();            System.out.println("获取到标签中的方法："+annoMethod[0].toString());            Field[] fields = classes.getDeclaredFields();            System.out.println("获取到标签中的字段："+ fields[0].toString());        }    }}