package org.xfl.API;/** * @program: learn * @description: * @author: xfl * @create: 2019-03-19 19:15 **/public class TestRandom {    public static void main(String[] args){        /**         * [0-1) double         */        System.out.println(Math.random());        double original = Math.random()*9 + 1;        System.out.println("before:---->" + original);        int flag = (int) (original*1000);        System.out.println("flag:--->" + flag);    }}