package org.xfl.API.reflect;import java.lang.reflect.Field;/** * @program: Java_Base * @description: * @author: xfl * @create: 2019-02-28 16:30 **/public class ReflectCopy{    public static <T> T copy(T original) throws IllegalAccessException, InstantiationException {        Class<?> clazz = original.getClass();        Object targetObj = clazz.newInstance();        for (Field field:clazz.getDeclaredFields()){            field.setAccessible(true);            Object value = field.get(original);            field.set(targetObj,value);        }        return (T)targetObj;    }}