package org.xfl.OOP;/** * @program: learn * @description: * @author: xfl * @create: 2018-12-18 16:02 **/public abstract class Environment {    public boolean isRich;    public boolean isHealth;    public boolean isHome;    public Environment(boolean isRich, boolean isHealth, boolean isHome) {        this.isRich = isRich;        this.isHealth = isHealth;        this.isHome = isHome;    }    boolean isGoodOrNot(){        if (isRich && isHealth && isHome){            return true;        } else {            return false;        }    }}