package org.xfl.OOP;/** * @program: learn * @description: * @author: xfl * @create: 2018-12-18 16:02 **/public abstract class Environment {    protected boolean isRich;    protected boolean isHelth;    protected boolean isHome;    public Environment(boolean isRich, boolean isHelth, boolean isHome) {        this.isRich = isRich;        this.isHelth = isHelth;        this.isHome = isHome;    }    boolean isGoodOrNot(){        if (isRich && isHelth && isHome){            return true;        } else {            return false;        }    }}