package org.xfl.OOP;/** * @program: learn * @description: * @author: xfl * @create: 2018-12-18 16:00 **/public class Jon extends Person implements TechnicalAbility, Behaviors {    public Jon(Environment environment, String name, int age) {        super(environment, name, age);    }    @Override    public void read() {        System.out.print(environment.isGoodOrNot() ? "Good Read.":"Bad Read.");    }    @Override    public void listen() {        System.out.print(environment.isGoodOrNot() ? "Good listen.":"Bad listen.");    }    @Override    public void write() {        System.out.print(environment.isGoodOrNot() ? "Good Write.":"Bad Write.");    }    @Override    public void say() {        System.out.print(environment.isGoodOrNot() ? "Good Say.":"Bad Say.");    }    @Override    public void clothes() {        System.out.print(environment.isGoodOrNot() ? "Wears Good Clothes.":"Wears Bad clothes.");    }    @Override    public void eat() {        System.out.print(environment.isGoodOrNot() ? "Eat Something Good.":"Eat Something Bad.");    }    @Override    public void walk() {    }    @Override    public void takeTheSubway() {        System.out.print(this.name + "Take the subway");    }    @Override    public void code(String order) {    }}