package org.xfl.OOP;import java.util.List;/** * @program: learn * @description: * @author: xfl * @create: 2018-12-18 15:50 **/public abstract class Person {    public Environment environment;    public String name;    public int age;    public boolean learn;    public Person(Environment environment,String name,int age) {        this.name = name;        this.age = age;        this.environment = environment;    }    public void setLearn(boolean learn) {        this.learn = learn;    }    protected List<String> disposition;    protected abstract void read();    protected abstract void listen();    protected abstract void write();    protected abstract void say();    protected abstract void clothes();    protected abstract void eat();    protected abstract void walk();}