package org.xfl.annotation;import org.slf4j.Logger;import org.slf4j.LoggerFactory;/** * @program: learn * @description: 如何使用自定义注解 * @author: xfl * @create: 2018-07-16 14:13 **/public class MyAnnotationTest {    public static final Logger LOGGER = LoggerFactory.getLogger(MyAnnotationTest.class);    @MyAnnotationVariable    String str = "myAnnotationTest";    @MyAnnotation    public void hold(){        LOGGER.info("hold方法上使用了注解");    }    @MyAnnotationWithValue()    public void holdWithValue(){        LOGGER.info("holdWithValue方法上使用了注解");    }}