package org.xfl.callByValue;/** * @program: learn * @description: 值传递 * @author: xfl * @create: 2018-09-07 16:15 **/public class CallByValue {    private static int x = 10;    public static int updateValue(int value){        return value = 3 * value;    }    /**     * 由结果可见 x的值被copy给value，经过updateValue函数value的值被修改，     * 而x的值没有发生改变     */    public static void main(String[] args){        System.out.println("调用前x的值：" + x);        int res = updateValue(x);        System.out.println("调用后x的值：" + x);        System.out.println("被修改过的value的值：" + res);    }}