package org.xfl.collection;import java.util.ArrayList;import java.util.Iterator;/** * @program: learn * @description: * @author: xfl * @create: 2019-09-23 14:14 **/public class IteratorRemove {    public static void main(String[] args){        ArrayList<Integer> arrayList = new ArrayList<>();        for (int i = 0;i < 10;i++) {            arrayList.add(i);        }        System.out.println("Remove before:" + arrayList.size());        Iterator<Integer> it = arrayList.iterator();        while (it.hasNext()) {            Integer a = it.next();            if (a == 4){                it.remove();            }        }        System.out.println("Removed:" + arrayList.size());    }}