package org.xfl.dataStructure;import java.util.function.BooleanSupplier;/** * @program: learn * @description: * @author: xfl * @create: 2018-10-08 17:39 **/public class test {    public static void main (String[] args){        if (getA()){            System.out.println("a");        } else {            System.out.println("b");        }    }    static Boolean getA(){        BooleanSupplier booleanSupplier = () ->{System.out.print("a"); return false;};        return booleanSupplier.getAsBoolean();    }}