package org.xfl.designMode.strategy;/** * @program: learn * @description: * @author: xfl * @create: 2018-12-18 10:17 **/public class Environment {    private Strategy strategy;    public Environment(Strategy strategy) {        this.strategy = strategy;    }    private int calculate(int a , int b){        return strategy.compute(a,b);    }    public static void main(String[] args){        Environment calculate = new Environment(new AddStrategy());        int res = calculate.calculate(1,1);        System.out.print("The compute res:" + res);    }}