package org.xfl.mutiThread.safe;public class TraditionThreadSynchronized {    public static void main(String[] args){        final Outputter outputter = new Outputter();        /**         * 两个不同的线程操作不同的数据，数据之间互不影响         */        new Thread(() -> outputter.output("mutiThread")).start();        new Thread(() -> outputter.output("runnable")).start();    }   /* static class Outputter{        public void output(String name){            for (int i = 0; i < name.length(); i++){                System.out.println(name.charAt(i));            }        }    }*/}