package org.xfl.objectInit;/** * @program: learn * @description: 对象初始化，父类 * @author: xfl * @create: 2018-07-23 10:29 **/public class Parent {    public Person person = new Person();    public static String own;    /**     * 姓名     */    String name;    /**     * 年龄     */    Integer age;    /**     * 父类think方法     */    void think(){        System.out.println("父类在思考。");    }    /**     * 父类代码块     */    {        this.age = 18;        System.out.println("父类代码块，init");    }    /**     * 父类静态代码块     */    static {        own = "parent's own";        System.out.println("父类静态代码块，init");    }    /**     * 父类无参数构造     */    public Parent(){        System.out.println("父类无参构造方法，init");    }    /**     * 父类有参数构造方法     * @param name     */    public Parent(String name){        this.name = name;        System.out.println("父类有参构造方法，init");    }    void pAction(){        System.out.println("父类非静态方法,init");    }    static void pStaticAction(){        System.out.println("父类静态方法,init");    }    public String getName() {        return name;    }    public Integer getAge() {        return age;    }    public void setName(String name) {        this.name = name;    }    public void setAge(Integer age) {        this.age = age;    }    @Override    public String toString() {        return "Parent{" +                "name='" + name + '\'' +                ", age=" + age +                '}';    }}