package org.xfl.objectInit;/** * @program: learn * @description: 用于成员变量初始化 * @author: xfl * @create: 2018-07-23 12:04 **/public class Person {    public Person(){        System.out.println("Person无参数构造,init");    }    public Person(String str){        System.out.println("Person有参构造执行,调用者:" + str);    }    static {        System.out.println("Person静态代码块,init");    }    {        System.out.println("Person代码块:init");    }}