package org.xfl.reflect;import org.xfl.annotation.Person;/** * @program: Java_Base * @description: * @author: xfl * @create: 2019-02-28 16:34 **/@Personpublic class ForCopy {    private String name;    private Integer age;    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public Integer getAge() {        return age;    }    public void setAge(Integer age) {        this.age = age;    }    public ForCopy() {    }    public ForCopy(String name, Integer age) {        this.name = name;        this.age = age;    }    @Override    public String toString() {        return "ForCopy{" +                "name='" + name + '\'' +                ", age=" + age +                '}';    }    class Copy{        private String copy;        public String getCopy() {            return copy;        }        public void setCopy(String copy) {            this.copy = copy;        }    }}