package org.xfl.reflect;import java.lang.reflect.Field;/** * @program: learn * @description: 反射 * @author: xfl * @create: 2018-07-16 15:42 **/public class MyReflect {    public static void main(String[] args) throws InstantiationException, IllegalAccessException {        ForCopy original = new ForCopy();        original.setAge(11);        original.setName("小明");        ForCopy res = ReflectCopy.copy(original);        System.out.println("Res for copy by Reflect:" + res.toString());        Class<?> clazz = original.getClass();        for (Field field:clazz.getDeclaredFields()){            field.setAccessible(true);            String name = field.getName();            Object value = field.get(original);            System.out.println(name + ":" + value );        }    }}