package org.xfl.reflect;import java.lang.reflect.Field;/** * @program: Java_Base * @description: * @author: xfl * @create: 2019-02-28 16:30 **/public class ReflectCopy{    public static <T> T copy(T obj) throws IllegalAccessException, InstantiationException {        Class<?> clazz = obj.getClass();        Object resObj = clazz.newInstance();        for (Field field:clazz.getDeclaredFields()){            field.setAccessible(true);            Object value = field.get(obj);            field.set(resObj,value);        }        return (T) resObj;    }}