package org.xfl.socket;import java.io.*;import java.net.Socket;/** * @program: learn * @description: * @author: xfl * @create: 2019-09-23 15:34 **/public class SocketClient {    public static void main(String[] args){        try {            Socket socket = new Socket("127.0.0.1",9999);            BufferedWriter writer = new BufferedWriter(new OutputStreamWriter(socket.getOutputStream()));            BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));            while (true){                String str = reader.readLine();                writer.write(str);                writer.write("\n");                writer.flush();            }        } catch (IOException e) {            e.printStackTrace();        }    }}