package org.xfl.socket;import java.io.IOException;import java.net.ServerSocket;import java.net.Socket;/** * @program: learn * @description: * @author: xfl * @create: 2019-09-23 15:34 **/public class SocketServer {    public static void main(String[] args){        try {            ServerSocket serverSocket = new ServerSocket(9999);            Socket socket = null;            while (true) {                socket = serverSocket.accept();            }        } catch (IOException e) {            e.printStackTrace();        }    }}