package org.xfl.socket;import java.io.*;import java.net.ServerSocket;import java.net.Socket;/** * @program: learn * @description: * @author: xfl * @create: 2019-09-23 15:34 **/public class SocketServer {    public static void main(String[] args){        try {            ServerSocket serverSocket = new ServerSocket(9999);            Socket socket = serverSocket.accept();            InputStream is = socket.getInputStream();            BufferedReader reader = new BufferedReader(new InputStreamReader(is));            String payload;            while ((payload = reader.readLine()) != null){                System.out.println(payload);            }        } catch (IOException e) {            e.printStackTrace();        }    }}