package org.xfl.thread;/** * @program: learn * @description: * @author: xfl * @create: 2018-07-25 10:12 **/public class TestThread extends Thread{    private String name;    public TestThread(String name) {        this.name = name;    }    @Override    public void run() {        super.run();        for (int i = 0;i < 5; i++){            System.out.println("线程-" +name + "运行：" + i);            try {                sleep(10);            } catch (InterruptedException e) {                e.printStackTrace();            }        }    }    public static void main(String[] args){        TestThread testThread = new TestThread("1");        TestThread testThread1 = new TestThread("2");        testThread.start();        testThread1.start();    }}