package org.xfl.springbootlearn;import org.springframework.boot.CommandLineRunner;import org.springframework.context.ApplicationContext;import org.springframework.context.annotation.Bean;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.Arrays;/** * @program: learn * @description: Hello Spring Boot * @author: xfl * @create: 2018-07-18 18:37 **/@RestControllerpublic class HelloSpringBoot {    @RequestMapping("/")    public String index(){        return "Greetings from Spring Boot!";    }    @Bean    public CommandLineRunner commandLineRunner(ApplicationContext context){        return args -> {            System.out.println("Let's inspect the bean provided by Spring Boot:");            String[] beanNames = context.getBeanDefinitionNames();            Arrays.sort(beanNames);            for (String beanName:beanNames) {                System.out.println(beanName);            }        };    }}