package org.xfl.springbootlearn.bean;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;import javax.persistence.Table;import java.math.BigDecimal;/** * @program: learn * @description: Test jpa * @author: xfl * @create: 2018-07-19 16:27 **/@Entity@Table(name = "account")public class Account {    /**     * 主键     */    @Id    @GeneratedValue    private Integer id;    /**     * 账户名称     */    private String name;    /**     * 账户金额      */    private BigDecimal money;    /**     * 账户所有人     */    private String ownner;    public Integer getId() {        return id;    }    public String getName() {        return name;    }    public BigDecimal getMoney() {        return money;    }    public String getOwnner() {        return ownner;    }    public void setId(Integer id) {        this.id = id;    }    public void setName(String name) {        this.name = name;    }    public void setMoney(BigDecimal money) {        this.money = money;    }    public void setOwnner(String ownner) {        this.ownner = ownner;    }    @Override    public String toString() {        return "Account{" +                "id=" + id +                ", name='" + name + '\'' +                ", money=" + money +                ", ownner='" + ownner + '\'' +                '}';    }}