package org.xfl.springbootlearn.bean;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.stereotype.Component;/** * @program: learn * @description: 配置文件属性赋值给实体类 * @author: xfl * @create: 2018-07-19 15:23 **/@Component@ConfigurationProperties(prefix = "her")public class ConfigBean {    private String name;    private Integer age;    private String sex;    private Integer number;    private String family;    public void setName(String name) {        this.name = name;    }    public void setAge(Integer age) {        this.age = age;    }    public void setSex(String sex) {        this.sex = sex;    }    public void setNumber(Integer number) {        this.number = number;    }    public void setFamily(String family) {        this.family = family;    }    public String getName() {        return name;    }    public Integer getAge() {        return age;    }    public String getSex() {        return sex;    }    public Integer getNumber() {        return number;    }    public String getFamily() {        return family;    }    @Override    public String toString() {        return "ConfigBean{" +                "name='" + name + '\'' +                ", age=" + age +                ", sex='" + sex + '\'' +                ", number=" + number +                ", family='" + family + '\'' +                '}';    }}