package org.xfl.springbootlearn.bean;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.context.annotation.PropertySource;import org.springframework.stereotype.Component;/** * @program: learn * @description: 自定义properties * @author: xfl * @create: 2018-07-19 15:42 **/@Component@PropertySource(value = "classpath:my.properties")@ConfigurationProperties(prefix = "my")public class MyPropertiesBean {    private String name;    private String sex;    private Integer age;    public void setName(String name) {        this.name = name;    }    public void setSex(String sex) {        this.sex = sex;    }    public void setAge(Integer age) {        this.age = age;    }    public String getName() {        return name;    }    public String getSex() {        return sex;    }    public Integer getAge() {        return age;    }    @Override    public String toString() {        return "MyPropertiesBean{" +                "name='" + name + '\'' +                ", sex='" + sex + '\'' +                ", age=" + age +                '}';    }}