package org.xfl.springbootlearn.dao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.jdbc.core.JdbcTemplate;import org.springframework.stereotype.Repository;import org.xfl.springbootlearn.bean.ConfigBean;/** * @program: learn * @description: Repository * @author: xfl * @create: 2018-07-19 15:58 **/@Repositorypublic class TestDaoImpl implements TestDao {    @Autowired    JdbcTemplate jdbcTemplate;    @Override    public int update(ConfigBean bean) {        return jdbcTemplate.update("UPDATE com_user_number SET name = ?,age = ? WHERE id = ?",bean.getName(),bean.getAge(),bean.getName());    }}