package org.xfl.springbootlearn.mapper;import org.apache.ibatis.annotations.*;import org.xfl.springbootlearn.bean.Account;import java.util.List;@Mapperpublic interface AccountMapper {    /**     * myBatis     * @param id     * @return     */    @Select("select * from account where id = #{id}")    Account findByID(Integer id);    /**     * ADD     * @param account     * @return     */    @Insert("insert into account(name,money) values(#{name},#{money})")    @Options(useGeneratedKeys = true)    Integer addAccount(Account account);    /**     * Update Account     * @param account     * @return     */    @Update("update account set name = #{name},money = #{money} where id = #{id}")    Integer updateAccount(Account account);    /**     * Del     * @param id     * @return     */    @Delete("delete from account where id = #{id}")    Integer deleteAccount(Integer id);    /**     * Find List     * @return     */    @Select("select * from account")    List<Account> findAll();}