package org.xfl.springbootlearn.mapper;import org.apache.ibatis.jdbc.SQL;import org.springframework.stereotype.Component;import org.springframework.util.StringUtils;import java.util.Map;/** * @program: learn * @description: MyBatis 动态SQL语句构建 * @author: xfl * @create: 2018-07-20 17:25 **/@Componentpublic class AccountSqlProvider {    public String get(Map<String,String> params){        String name = params.get("name");        String owner = params.get("owner");        SQL sql = new SQL().SELECT("*").FROM("account");        if (!StringUtils.isEmpty(name)){            sql.WHERE("name = #{name}");        }        if (!StringUtils.isEmpty(owner)){            sql.WHERE("owner = #{owner}");        }        return sql.toString();    }}